import React, { useState, useEffect } from "react";

import { Bar } from "react-chartjs-2";
import { getData } from "../data/apiData";

const BarChart = () => {
  const [labelsData, setLabelsData] = useState([]);
  const [valuesData, setValuesData] = useState([]);

  const data = (canvas) => {
    const ctx = canvas.getContext("2d");

    return {
      labels: [],
      datasets: [
        {
          barPercentage: 0.5,
          barThickness: 10,
          label: "",
          data: [],
          borderWidth: 1,
        },
      ],
    };
  };
  const options = {
    responsive: true,
  };

  const getChartDta = async () => {
    try {
      let labelsDataArray = [];
      let valuesDataArray = [];
      const data = await getData();
      data.forEach((elem) => {
        labelsDataArray.push(elem.cia);
        valuesDataArray.push(elem.qtde_vitima);
      });
      console.log(data);
    } catch (e) {
      console.log(e);
    }
  };
  useEffect(() => {
    getChartDta();
  }, []);
  return <Bar data={data} options={options} />;
};

export default BarChart;
