import React, { useState, useEffect } from "react";
import fireDb from "../firebase";
import Chart from "react-google-charts";

const Charts = () => {
  const [dadosOcorrencias, setDadosOcorrencias] = useState([]);

  const dadosOc = Object.keys(dadosOcorrencias).map(function (oc) {
    return dadosOcorrencias[oc];
  });

  useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dados = [];
        const labels = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          //console.log(data.tipo_2);
          dados.push(data.qtde_vitima).map((str) => parseInt(str));
          labels.push(data.data);
        });
        setDadosOcorrencias([dados, labels]);
        //console.log(dados);
      })
      .catch((error) => console.log(error));
  }, []);

  console.log(dadosOcorrencias);
  return (
    <>
      <div style={{ display: "flex" }}>
        <Chart
          width={400}
          height={"300px"}
          chartType="AreaChart"
          loader={<div>Loading Chart</div>}
          data={dadosOcorrencias}
          options={{
            title: "Ocorrencias",
            hAxis: { title: "Year", titleTextStyle: { color: "#333" } },
            vAxis: { minValue: 0 },
            // For the legend to fit, we make the chart area smaller
            chartArea: { width: "50%", height: "70%" },
            // lineWidth: 25
          }}
          legendToggle
        />
      </div>
    </>
  );
};

export default Charts;
