import React, { useState, useEffect } from 'react';
import fireDb from '../firebase';

const Consulta = () => {

    let [ dadosOcorrencias, setDadosOcorrencias] = useState({})
    var [ currentId, setCurrentId ] = useState('')

    useEffect(() => {
        fireDb.firestore().collection('ocgeral').get().then( snapshot => {
            const dados = []
            snapshot.forEach( doc => {
                const data = doc.data()
                dados.push(data)
            })
            setDadosOcorrencias(dados)
            //console.log(snapshot)
        }).catch( error => console.log(error) )
    }, [])


    return (
        <>
            <h1> PÃ¡gina de consultas </h1>
            <div className="col-md-12">
                <table className="table table-boderless table-striped">
                    <thead className="thead-light">
                        <tr>
                            <td>Data</td>
                            <td>Hora</td>
                            <td>Cidade</td>
                            <td>CIA</td>
                            <td>Natureza</td>
                            <td>Tipo</td>
                            <td>ROP</td>
                        </tr>

                    </thead>
                </table>
            </div>
        </>
    )
}

export default Consulta;
