import React, { useState, useEffect } from "react";
import fireDb from "../firebase";
import { Bar, Line } from "react-chartjs-2";

const Charts = () => {
  const [dadosOcorrencias, setDadosOcorrencias] = useState({});

  const [chartData, setChartData] = useState({});
  const [qtdVictim, setQtdVictim] = useState([]);
  const [cities, setCities] = useState([]);

  const dadosOc = Object.keys(dadosOcorrencias).map(function (oc) {
    return dadosOcorrencias[oc];
  });

  useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dados = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          dados.push(data);
        });
        setDadosOcorrencias(dados);
      })
      .catch((error) => console.log(error));
  }, []);

  useEffect(() => {
    chart();
  }, []);

  const chart = () => {
    let victim = [];
    let city = [];
    for (const obj of dadosOc) {
      victim.push(parseInt(obj.qtde_vitima));
      city.push(obj.cidade)
    }
    console.log(victim)
    console.log(city)

    setChartData({
      labels: ["Canindé", "Caridade", "Paramoti"],
      datasets: [
        {
          label: "qtde de vitimas",
          data: [32, 45, 56],
          backgroundColor: ["rgba(75,192,192,0.6)"],
          borderWidth: 4,
        },
      ],
    });
  };

  console.log(dadosOcorrencias)

  return (
    <>
      <h3>Gráfico aqui</h3>
      <div style={{ width: "300px" }}>
        <div>
          <Bar
            data={chartData}
            options={{
              responsive: true,
              title: { text: "Qtde Vitimas", display: true },
              scales: {
                yAxes: [
                  {
                    ticks: {
                      autoSkip: true,
                      maxTicksLimit: 10,
                      beginAtZero: true,
                    },
                    gridLines: {
                      display: false,
                    },
                  },
                ],
                xAxes: [
                  {
                    gridLines: {
                      display: false,
                    },
                  },
                ],
              },
            }}
          />
        </div>
        <div>
          <Line
            data={chartData}
            options={{
              responsive: true,
              title: { text: "Qtde Vitimas", display: true },
              scales: {
                yAxes: [
                  {
                    ticks: {
                      autoSkip: true,
                      maxTicksLimit: 10,
                      beginAtZero: true,
                    },
                    gridLines: {
                      display: false,
                    },
                  },
                ],
                xAxes: [
                  {
                    gridLines: {
                      display: false,
                    },
                  },
                ],
              },
            }}
          />
        </div>
      </div>
    </>
  );
};

export default Charts;
