        const dados = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          dados.push(data);
        });
        setDadosOcorrencias(dados);
        //console.log(dados);
      })
      .catch((error) => console.log(error));
  }, []); */



  return (
    <>
      <h3>Gráfico aqui</h3>
      <div style={{ width: "300px" }}>
        <Bar data={chartData} options={{ 
          responsive: true,
          title: { text: "Qtde Vitimas", display: true},
          scales: { 
            yAxes: [{
              ticks: {
                autoSkip: true,
                maxTicksLimit: 10,
                beginAtZero: true
              },
              gridLines: {
                
              }
            }]
          }
        }}/>
      </div>
    </>
  );
};

export default Charts;
