import React, { useState, useEffect } from "react";
import fireDb from "../firebase";
import { Bar, Line } from "react-chartjs-2";
import { Card } from "react-bootstrap";

const Charts = () => {
  const [dadosOcorrencias, setDadosOcorrencias] = useState({});

  const [chartData, setChartData] = useState({});
  const [qtdVictim, setQtdVictim] = useState([]);
  const [cities, setCities] = useState([]);

  const dadosOc = Object.keys(dadosOcorrencias).map(function (oc) {
    return dadosOcorrencias[oc];
  });

  const chart = () => {
    let victim = [];
    let cia = [];
    for (const obj of dadosOc) {
      victim.push(parseInt(obj.qtde_vitima))
      cia.push(obj.cia)
    }
    setChartData({
      labels: cia,
      datasets: [
        {
          label: "qtde de vitimas",
          data: victim,
          backgroundColor: ["rgba(75,192,192,0.6)"],
          borderWidth: 0,
        },
      ],
    });
    console.log(victim, cia)
  };

  useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dados = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          dados.push(data);
        });
        setDadosOcorrencias(dados);
      }).catch((error) => console.log(error));
  }, []);


  return (
    <>
    <div className="content">
      <h3>Gráfico aqui</h3>
      <button onClick={chart}>Carregar gráficos</button>
      <div style={{ width: "500px" }}>
        <div>
          <Bar
            data={chartData}
            options={{
              responsive: true,
              title: { text: "Qtde Vitimas", display: true },
              scales: {
                yAxes: [
                  {
                    ticks: {
                      autoSkip: true,
                      maxTicksLimit: 10,
                      beginAtZero: true,
                    },
                    gridLines: {
                      display: false,
                    },
                  },
                ],
                xAxes: [
                  {
                    gridLines: {
                      display: false,
                    },
                  },
                ],
              },
            }}
          />
        </div>
        </div>
      </div>
    </>
  );
};

export default Charts;
