import React, { useState, useEffect } from "react";
import fireDb from "../firebase";
import { SearchOutlined } from "@ant-design/icons";
import { Input, Space } from 'antd';
const { Search } = Input;

const Consulta = () => {
  let [dadosOcorrencias, setDadosOcorrencias] = useState({});
  //var [ currentId, setCurrentId ] = useState('')

  useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dados = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          dados.push(data);
        });
        setDadosOcorrencias(dados);
        console.log(dados);
      })
      .catch((error) => console.log(error));
  }, []);

  return (
    <>
      <h3 align="center"> CONSULTA DE OCORRÊNCIAS </h3>
      <Search placeholder="input search text" enterButton />
      <form className="row g-3">
        <div className="col-md-4">
          <input type="text" className="form-control" name="consulta" placeholder="Digite sua consulta" />
        </div>
      </form>
      <div className="dropdown-divider"></div>
      <div className="col-md-12">
        <table className="table table-striped table-sm">
          <thead className="thead-light">
            <tr>
              <td>Data</td>
              <td>Hora</td>
              <td>Cidade</td>
              <td>CIA</td>
              <td>Natureza</td>
              <td>Tipo</td>
              <td>ROP</td>
            </tr>
          </thead>

          <tbody>
            {Object.keys(dadosOcorrencias).map((id) => {
              return (
                <tr key={id}>
                  <td> {dadosOcorrencias[id].data} </td>
                  <td> {dadosOcorrencias[id].hora} </td>
                  <td> {dadosOcorrencias[id].cidade} </td>
                  <td> {dadosOcorrencias[id].cia} </td>
                  <td> {dadosOcorrencias[id].natureza} </td>
                  <td> {dadosOcorrencias[id].tipo} </td>
                  <td> {dadosOcorrencias[id].num_rop} </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default Consulta;
