import React, { useState, useEffect } from "react"
import fireDb from "../firebase";
//import { SearchOutlined } from "@ant-design/icons";
import { Input } from "antd";
import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";
//const { Search } = Input;

const Consulta = () => {
  let [dadosOcorrencias, setDadosOcorrencias] = useState({});
  //var [ currentId, setCurrentId ] = useState('')

  const [searchData, setSearchData] = useState('');
  const tbody = document.getElementById("tbody");

  useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dados = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          dados.push(data);
        });
        setDadosOcorrencias(dados);
        console.log(dados);
      })
      .catch((error) => console.log(error));
  }, []);

  /* const handleSearch = () => {
    var busca = document.getElementById("iDconsulta").value.toLowerCase();
    
    for (var i = 0; i < tbody.childNodes.length; i++) {
      var found = false;
      var tr = tbody.childNodes[i];
      var td = tr.childNodes;
      console.log(td)

      for (var j = 0; j < td.length; j++) {
        var value = td[j].childNodes[0].nodeValue.toLowerCase();

        if (value.indexOf(busca) >= 0) {
          found = true;
        }
      }
      if (found) { tr.style.display = "table-row"; }
      else { tr.style.display = "none"; }
    }
  } */


  return (
    <>
      <h3 align="center"> CONSULTA DE OCORRÃŠNCIAS </h3>

      <form className="row g-3">
        <div className="col-md-4">
          <input
            name="consulta"
            id="iDconsulta"
            autoComplete="off"
            placeholder="Consultar"
            type="search"
            className="form-control search-input"
          />
        </div>
      </form>

      <div className="dropdown-divider"></div>

      <div className="col-md-12">
        <table className="table table-striped table-sm">
          <thead className="thead-light">
              <tr>
                <td>Data</td>
                <td>Hora</td>
                <td>Cidade</td>
                <td>CIA</td>
                <td>Natureza</td>
                <td>Tipo</td>
                <td>ROP</td>
              </tr>
            </thead>
            <tbody id="tbody">
              {dadosOcorrencias.map((item) => {

                return(
                  <tr key={item.id}>
                    <td>{item.data}</td>
                    <td>{item.hora}</td>
                    <td>{item.cidade}</td>
                    <td>{item.cia}</td>
                    <td>{item.natureza}</td>
                    <td>{item.tipo}</td>
                    <td>{item.num_rop}</td>
                  </tr>
                )

              }
                
              )}
            </tbody>
        </table>
      </div>
    </>
  );
};

export default Consulta;
