import React, { useState } from "react";
import { Form, DatePicker, TimePicker, Select, Input, Button } from "antd";

const { Option } = Select;

const FormularioCadastro = () => {
  const initialData = {
    bairro: "",
    bpm: "",
    cia: "",
    cidade: "",
    data: "",
    hora: "",
    latitude: "",
    longitude: "",
    naturezal: "",
    num_rop: "",
    procedimento: "",
    qtde_arma_fogo_ap: "",
    qtde_droga_ap: "",
    qtde_municoes_ap: "",
    qtde_vitima: "",
    tipo: "",
    tipo_2: "",
    tipo_arma: "",
    arquivo_rop: "",
  };

  let { values, setValues } = useState(initialData);

  const onFinish = e => {
      let { name, value } = e.target

      setValues({
          ...values,
          [name] : value
      })
    console.log("Received values of form: ", values);
  };

  return (
    <>

        <form>
        <div class="mb-3">
            <label for="exampleInputEmail1" class="form-label">Email address</label>
            <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
            <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
        </div>
        <div class="mb-3">
            <label for="exampleInputPassword1" class="form-label">Password</label>
            <input type="password" class="form-control" id="exampleInputPassword1"/>
        </div>
        <div class="mb-3 form-check">
            <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
            <label class="form-check-label" for="exampleCheck1">Check me out</label>
        </div>
        <button type="submit" class="btn btn-primary">Submit</button>
        </form>



      <Form
        name="cadoc"
        initialValues={{ remember: true }}
        layout="horizontal"
        labelCol={{ span: 6 }}
        wrapperCol={{ span: 9 }}
        onFinish={onFinish}
      >
        <Form.Item
          label="Data Ocorrência"
          name="data"
          rules={[{ required: true, message: "Insira a data da ocorrência!" }]}
          value={values.data}
        >
          <DatePicker />
        </Form.Item>

        <Form.Item
          label="Hora Ocorrência"
          name="hora"
          rules={[{ required: true, message: "Insira a hora da ocorrência!" }]}
          value={values.hora}
        >
          <TimePicker />
        </Form.Item>

        <Form.Item
          label="Cia"
          name="cia"
          rules={[{ required: true, message: "Insira a Companhia!" }]}
          value={values.cia}
        >
          <Select placeholder="Selecione a CIA">
            <Option value="1CIA">1ªCia</Option>
            <Option value="3CIA">3ªCia</Option>
            <Option value="4CIA">4ªCia</Option>
          </Select>
        </Form.Item>

        <Form.Item
          label="Cidade Ocorrência"
          name="cidade"
          rules={[
            { required: true, message: "Insira a cidade da ocorrência!" },
          ]}
          value={values.cidade}
        >
          <Input />
        </Form.Item>

        <Form.Item
          wrapperCol={{
            span: 12,
            offset: 6,
          }}
        >
          <Button type="primary" htmlType="submit">
            Salvar
          </Button>
        </Form.Item>
      </Form>
    </>
  );
};

export default FormularioCadastro;
