import React, { useState } from "react";


const FormularioCadastro = () => {
  const initialData = {
    bairro: "",
    bpm: "",
    cia: "",
    cidade: "",
    data: "",
    hora: "",
    latitude: "",
    longitude: "",
    naturezal: "",
    num_rop: "",
    procedimento: "",
    qtde_arma_fogo_ap: "",
    qtde_droga_ap: "",
    qtde_municoes_ap: "",
    qtde_vitima: "",
    tipo: "",
    tipo_2: "",
    tipo_arma: "",
    arquivo_rop: "",
  };

  let { values, setValues } = useState(initialData);

  const handleSubmit = e => {
      let { name, value } = e.target

      setValues({
          ...values,
          [name] : value
      })
    console.log("Received values of form: ", values);
  };

  return (

        <form className="row g-3">
        
            <div className="col-6">
                <label for="inputData" class="form-label">Data: </label>
                <input type="date" className="form-control" placeholder="Data da Ocorrência" value={values.data}/>
            </div>
            <div className="col-6">
                <label for="inputHora" class="form-label">Hora: </label>
                <input type="time" className="form-control" placeholder="Hora da Ocorrência" value={values.hora}/>
            </div>
            <div className="col-12">
            <button type="submit" className="btn btn-primary">Registrar</button>
            </div>

        </form>

  );
};

export default FormularioCadastro;
