import React, { useState, useEffect } from "react";
import fireDb from "../firebase";
import { Bar, Line } from "react-chartjs-2";

const Charts = () => {
  const [dadosOcorrencias, setDadosOcorrencias] = useState({});

  const [chartData, setChartData] = useState({});
  const [qtdVictim, setQtdVictim] = useState([]);

  const chart = () => {
    setChartData({
      labels: ["Canindé", "Caridade", "Paramoti"],
      datasets: [
        {
          label: "qtde de vitimas",
          data: [32, 45, 56],
          backgroundColor: ["rgba(75,192,192,0.6)"],
          borderWidth: 4,
        },
      ],
    });
  };

  useEffect(() => {
    chart();
  }, []);

  /*   useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dados = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          dados.push(data);
        });
        setDadosOcorrencias(dados);
        //console.log(dados);
      })
      .catch((error) => console.log(error));
  }, []); */

  return (
    <>
      <h3>Gráfico aqui</h3>
      <div style={{ width: "300px" }}>
        <Bar
          data={chartData}
          options={{
            responsive: true,
            title: { text: "Qtde Vitimas", display: true },
            scales: {
              yAxes: [
                {
                  ticks: {
                    autoSkip: true,
                    maxTicksLimit: 10,
                    beginAtZero: true,
                  },
                  gridLines: {
                    display: false,
                  },
                },
              ],
              xAxes: [
                {
                  gridLines: {
                    display: false,
                  },
                },
              ],
            },
          }}
        />
        <Bar
          data={chartData}
          options={{
            responsive: true,
            title: { text: "Qtde Vitimas", display: true },
            scales: {
              yAxes: [
                {
                  ticks: {
                    autoSkip: true,
                    maxTicksLimit: 10,
                    beginAtZero: true,
                  },
                  gridLines: {
                    display: false,
                  },
                },
              ],
              xAxes: [
                {
                  gridLines: {
                    display: false,
                  },
                },
              ],
            },
          }}
        />
      </div>
    </>
  );
};

export default Charts;
