import React, { useState, useEffect } from "react";
import fireDb from "../firebase";

const Charts = () => {
  const [dadosOcorrencias, setDadosOcorrencias] = useState({});

  useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dadosArray = [];
        const labels = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          //const municoes = parseInt(data.qtde_municoes_ap);
          //console.log(municoes);
          dadosArray.push(data.qtde_municoes_ap);
          labels.push(data.cia);
        });
        const dados = dadosArray.map((i) => Number(i));
        setDadosOcorrencias([labels, dados]);
      })
      .catch((error) => console.log(error));
  }, []);

  console.log(dadosOcorrencias);

  /* var arrayString = JSON.stringify(dadosOcorrencias);
  var stringJson = JSON.parse(arrayString);
  console.log(stringJson); */

  return (
    <>
      <h3>Gr√°fico aqui</h3>
    </>
  );
};

export default Charts;
