import React, { useState, useEffect } from "react";
import fireDb from "../firebase";

const Charts = () => {
  const [dadosOcorrencias, setDadosOcorrencias] = useState({});

  useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dados = [];
        const labels = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          const municoes = parseInt(data.qtde_municoes_ap);
          console.log(municoes);
          dados.push(municoes);
          labels.push(data.cia);
        });
        //setDadosOcorrencias(dados);
        console.log(dados);
      })
      .catch((error) => console.log(error));
  }, []);

