import React, { useState, useEffect } from "react"
import fireDb from "../firebase";

const Consulta = () => {
  const [dadosOcorrencias, setDadosOcorrencias] = useState({});
  const [searchTerm, setSearchTerm] = useState('');

  

  useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dados = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          dados.push(data);
        });
        setDadosOcorrencias(dados);
        console.log(dados);
      })
      .catch((error) => console.log(error));
  }, []);


  return (
    <>
      <h3 align="center"> CONSULTA DE OCORRÃŠNCIAS </h3>

      <form className="row g-3">
        <div className="col-md-4">
          <input
            name="consulta"
            id="iDconsulta"
            autoComplete="off"
            placeholder="Consultar"
            type="search"
            className="form-control search-input"
            onChange= { (e) => setSearchTerm(e.target.value)}
          />
          {searchTerm}
        </div>
      </form>

      <div className="dropdown-divider"></div>

      <div className="table-responsive">
        <table className="table table-striped table-sm" data-page-size="15" data-pagination="true">
          <thead className="thead-light">
              <tr>
                <td>Data</td>
                <td>Hora</td>
                <td>Cidade</td>
                <td>CIA</td>
                <td>Natureza</td>
                <td>Tipo</td>
                <td>ROP</td>
              </tr>
            </thead>
            <tbody id="tbody">
              {Object.keys(dadosOcorrencias).map((item) => {

                return(
                  <tr key={item}>
                    <td>{dadosOcorrencias[item].data}</td>
                    <td>{dadosOcorrencias[item].hora}</td>
                    <td>{dadosOcorrencias[item].cidade}</td>
                    <td>{dadosOcorrencias[item].cia}</td>
                    <td>{dadosOcorrencias[item].natureza}</td>
                    <td>{dadosOcorrencias[item].tipo}</td>
                    <td>{dadosOcorrencias[item].num_rop}</td>
                  </tr>
                )
              }                
              )}
            </tbody>
        </table>
      </div>
    </>
  );
};

export default Consulta;
