import React, { useState, useEffect } from "react"
import fireDb from "../firebase";
//import { SearchOutlined } from "@ant-design/icons";
import { Input } from "antd";
import { BootstrapTable, TableHeaderColumn } from "react-bootstrap-table";
//const { Search } = Input;

const Consulta = () => {
  const [dadosOcorrencias, setDadosOcorrencias] = useState({});
  const [searchData, setSearchData] = useState('');

  /* const dadosFiltrados = dadosOcorrencias.filter( oc => {
    return oc.cidade.toLowerCase().includes(searchData.toLowerCase());
  }) */

  useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dados = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          dados.push(data);
        });
        setDadosOcorrencias(dados);
        console.log(dados);
      })
      .catch((error) => console.log(error));
  }, []);


  return (
    <>
      <h3 align="center"> CONSULTA DE OCORRÃŠNCIAS </h3>

      <form className="row g-3">
        <div className="col-md-4">
          <input
            name="consulta"
            id="iDconsulta"
            autoComplete="off"
            placeholder="Consultar"
            type="search"
            className="form-control search-input"
            /* onChange= { e => setSearchData(e.target.value)} */
          />
          {searchData}
        </div>
      </form>

      <div className="dropdown-divider"></div>

      <div className="table-responsive">
        <table className="table table-striped table-sm" data-page-size="15" data-pagination="true">
          <thead className="thead-light">
              <tr>
                <td>Data</td>
                <td>Hora</td>
                <td>Cidade</td>
                <td>CIA</td>
                <td>Natureza</td>
                <td>Tipo</td>
                <td>ROP</td>
              </tr>
            </thead>
            <tbody id="tbody">
              {dadosOcorrencias.map((item) => {

                return(
                  <tr key={item.id}>
                    <td>{item.data}</td>
                    <td>{item.hora}</td>
                    <td>{item.cidade}</td>
                    <td>{item.cia}</td>
                    <td>{item.natureza}</td>
                    <td>{item.tipo}</td>
                    <td>{item.num_rop}</td>
                  </tr>
                )

              }
                
              )}
            </tbody>
        </table>
      </div>
    </>
  );
};

export default Consulta;
