import React, {useState} from 'react';
import { Form, DatePicker, TimePicker, Select, Input, Button } from 'antd';

const { Option } = Select;

const tailLayout = {
    wrapperCol: {
      offset: 8,
      span: 16,
    },
  };

const FormularioCadastro = () => {

    const initialData = {
        bairro: '',
        bpm: '',
        cia:'',
        cidade:'',
        data: '',
        hora: '',
        latitude: '',
        longitude: '',
        naturezal: '',
        num_rop: '',
        procedimento: '',
        qtde_arma_fogo_ap: '',
        qtde_droga_ap: '',
        qtde_municoes_ap: '',
        qtde_vitima:'',
        tipo:'',
        tipo_2: '',
        tipo_arma: '',
        arquivo_rop:''
    }

    let {values, setValues} = useState(initialData)


    return (
        <>
        <Form
            name="cadoc"
            initialValues={{ remember: true }}
            layout="horizontal"
            labelCol={{ span: 8 }} wrapperCol={{ span: 16 }}
        >
            <Form.Item
                label="Data Ocorrência"
                name="data"
                rules={[{ required: true, message: 'Insira a data da ocorrência!' }]}
                value= {values.data}
            >
                <DatePicker />
            </Form.Item>

            <Form.Item
                label="Hora Ocorrência"
                name="hora"
                rules={[{ required: true, message: 'Insira a hora da ocorrência!' }]}
                value= {values.hora}
            >
                <TimePicker />
            </Form.Item>

            <Form.Item
                label="Cia"
                name="cia"
                rules={[{ required: true, message: 'Insira a Companhia!' }]}
                value= {values.cia}
            >
                <Select placeholder="Selecione a CIA">
                    <Option value="1CIA">1ªCia</Option>
                    <Option value="3CIA">3ªCia</Option>
                    <Option value="4CIA">4ªCia</Option>
                </Select>
            </Form.Item>

            <Form.Item
                label="Cidade Ocorrência"
                name="cidade"
                rules={[{ required: true, message: 'Insira a cidade da ocorrência!' }]}
                value= {values.cidade}
            >
                <Input />
            </Form.Item>

            <Form.Item >
                <Button type="primary" htmlType="submit">
                Salvar
                </Button>
            </Form.Item>


        </Form>
        </>
    )
}

export default FormularioCadastro;
