import React, { useState, useEffect } from "react";
import fireDb from "../firebase";
//import { SearchOutlined } from "@ant-design/icons";
import { Input } from "antd";
//const { Search } = Input;

const Consulta = () => {
  let [dadosOcorrencias, setDadosOcorrencias] = useState({});
  //var [ currentId, setCurrentId ] = useState('')

  useEffect(() => {
    fireDb
      .firestore()
      .collection("ocgeral")
      .get()
      .then((snapshot) => {
        const dados = [];
        snapshot.forEach((doc) => {
          const data = doc.data();
          dados.push(data);
        });
        setDadosOcorrencias(dados);
        console.log(dados);
      })
      .catch((error) => console.log(error));
  }, []);

  const [busca, setBusca] = useState("");

  const ocFiltradas = dadosOcorrencias.filter((oc) => oc.startsWith("busca"));

  return (
    <>
      <h3 align="center"> CONSULTA DE OCORRÃŠNCIAS </h3>

      <form className="row g-3">
        <div className="col-md-4">
          <input
            name="consulta"
            id="txt_consulta"
            placeholder="Consultar"
            type="text"
            className="form-control"
            value={busca}
            onChange={(e) => setBusca(e.target.value)}
          />
        </div>
      </form>
      <div className="dropdown-divider"></div>
      <div className="col-md-12">
        <table className="table table-striped table-sm">
          <thead className="thead-light">
            <tr>
              <td>Data</td>
              <td>Hora</td>
              <td>Cidade</td>
              <td>CIA</td>
              <td>Natureza</td>
              <td>Tipo</td>
              <td>ROP</td>
            </tr>
          </thead>

          <tbody>
            {Object.keys(ocFiltradas).map((id) => {
              return (
                <tr key={id}>
                  <td> {ocFiltradas[id].data} </td>
                  <td> {ocFiltradas[id].hora} </td>
                  <td> {ocFiltradas[id].cidade} </td>
                  <td> {ocFiltradas[id].cia} </td>
                  <td> {ocFiltradas[id].natureza} </td>
                  <td> {ocFiltradas[id].tipo} </td>
                  <td> {ocFiltradas[id].num_rop} </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default Consulta;
